import React from 'react';

/**
 * Renders entry.description with optional descriptionLinks (inline links) or links (appended links).
 * Used by EntryItem and WorkEntry.
 */
export function renderDescription(entry) {
  const description = entry.description || '';
  const descriptionLinks = entry.descriptionLinks || [];
  const links = entry.links || [];

  if (descriptionLinks.length > 0) {
    const parts = [];
    let remaining = description;

    descriptionLinks.forEach((link) => {
      const idx = remaining.indexOf(link.text);
      if (idx !== -1) {
        if (idx > 0) {
          parts.push(remaining.substring(0, idx));
        }
        parts.push(
          <a key={link.url} href={link.url} target="_blank" rel="noopener noreferrer">
            {link.text}
          </a>
        );
        remaining = remaining.substring(idx + link.text.length);
      }
    });

    if (remaining) {
      parts.push(remaining);
    }

    return parts;
  }

  if (links.length > 0) {
    const parts = [description];

    links.forEach((link, i) => {
      if (i > 0) {
        parts.push(', ');
      } else {
        parts.push(' ');
      }
      parts.push(
        <a key={link.url} href={link.url} target="_blank" rel="noopener noreferrer">
          {link.text}
        </a>
      );
    });

    return parts;
  }

  return description;
}
